export const TokenIcon = ({
  className,
  icon,
}: {
  className?: string;
  icon?: string;
}) => {
  let tokenIcon: string | undefined;
  switch (icon) {
    case "power":
      tokenIcon = power;
      break;
    case "busd":
      tokenIcon = busd;
      break;
    case "bnb":
      tokenIcon = bnb;
      break;
    case "1inch":
      tokenIcon = oneInch;
      break;
    case "eth":
      tokenIcon = eth;
      break;
    case "lite":
      tokenIcon = lite;
      break;
    default:
      tokenIcon = btc;
      break;
  }
  return <img src={tokenIcon} alt={icon} className={className} />;
};

